class NagaCanonInfo {
  constructor() {
    this.RepoBase = 'https://github.com/flyilc/Buddhism/blob/master/乾隆大藏經(龍藏)TXT版_繁體';

    this.groups = [];
    var a = NagaCanon.split('\n');
    for (var i in a) {
      var coll = a[i].split('\|'), len = coll.length;
      var wg = new WorkGroup(coll[0]);
      this.groups.push(wg);
      for (var j=1; j<len; ++j)
        wg.addWork(new WorkInfo(coll[j]));
    }
  }
  totalWorks() { return this.groups.reduce((accu,cur) => accu+cur.works.length, 0); }
  totalVols() { return this.groups.reduce((accu,cur) => accu+cur.totalVols(), 0); }
  getWork(workId) { return ''; } // TODO
  getGroup(workId) { return ''; } // TODO
  getFirstVolFilename(workId) { return ''; } // TODO
  getURL(workId) {
    var fn = this.getFirstVolFilename(workId);
    if (!fn) {
      var wk = this.getWork(workId);
      fn = `第${workId}部～${wk.title}${wk.volCntDisp}`;
    }
    return `${this.RepoBase}/${this.getGroup(workId)}/${fn}.txt`;
  }
}

class WorkGroup {
  constructor(name) { this.name = name; this.works = []; }
  getWorksDispList() { return this.works.map((w) => w.titleDisp()); }
  totalVols() { return this.works.reduce((accu,cur) => accu+cur.volCnt, 0); }
  addWork(wk) { this.works.push(wk); return this; }
}

class WorkInfo {
  constructor(ln) {
    // this.id;
    // this.title;
    // this.volCnt;
    // this.volCntDisp;

    var idx = ln.indexOf('】'), j;
    if (idx > 0) {
      this.id = ln.substring(1,idx);
      ln = this.title = ln.substring(idx+1);
    } else {
      this.title = ln;
    }
    if (!ln.endsWith('卷')) {
      this.volCnt = 1;
      this.volCntDisp = '';
    } else {
      for (j=ln.length-2; j>0 && isZDigit(ln[j]); --j);
      this.volCnt = parseZNumber(ln.substring(j+1, ln.length-1));
      this.volCntDisp = ln.substring(j+1);
      this.title = ln.substring(0, j+1);
    }
  }
  titleDisp() {
    return `【${this.id}】` +
           (!this.volCntDisp ? this.title : `${this.title}<small>${this.volCntDisp}</small>`);
  }
}

