// 正體字化轉換

class Buffer {
  constructor() { this.bufList = Array.from(arguments); }

  w() {
    var ret = '';
    for (var i in arguments) {
      var x = arguments[i];
      if (x || (typeof x == 'number')) ret += x;
    }
    if (ret.length) this.bufList.push(ret);
    return (this.bufList.length < 1024) ? this : this.condense();
  }

  wIf() {
    if (arguments[0]) { arguments[0] = ''; this.w.apply(this, arguments); }
    return this;
  }

  wIfElse(cond, a, b) { return this.w(cond ? a : b); }

  append(s) { this.bufList.push(s); } // for performance

  prepend() {
    var ret = '';
    for (var i in arguments) {
      var x = arguments[i];
      x && (ret += x);
    }
    if (ret) this.bufList.unshift(ret);
    return this;
  }

  wrap(before, after) { return this.prepend(before).w(after); }

  // renders to one or more elements,
  // or an object with a write() function.
  // returns the text, and clears internally.
  render() {
    var ret = this.text();
    for (var i in arguments) {
      var k = arguments[i];
      if (!k) continue;
      if (typeof k == 'function')
        k(ret);
      else if (k.write)
        k.write(ret);
      else {
        var el = k.hasOwnProperty('innerHTML') ? k : e(k);
        el && (el.innerHTML = ret);
      }
    }
    this.bufList = [];
    return ret;
  }

  text() {
    var s = this.bufList.join('');
    this.bufList = [ s ];
    return s;
  }

  condense() {
    if (this.bufList.length > 100)
      this.text();
    return this;
  }

} // end of Buffer.

class Astral {
  constructor(str) {
    this.isAstral = true;
    this.original = str.isAstral ? str.getAll() : str;
    this.indices = [];
    var idx = 0;
    for (var i=0; i<this.original.length; ++i, ++idx) {
      this.indices.push(idx);
      var code = this.original.charCodeAt(i);
      if (code >= 0xd800 && code <= 0xdbff) { // skip the 2nd
        ++i;
        ++idx;
      }
    }
    this.length = this.indices.length;
  }
  append(s) {
    var ast = (s.original && s.indices) ? s : new Astral(s),
        olen = this.original.length;
    this.original += ast.original;
    for (var i=0; i<ast.length; ++i)
      this.indices.push(olen + ast.indices[i]);
    this.length = this.indices.length;
    return this;
  }
  getLength() { return this.indices.length; } // safer than this.length
  toString() { return this.original; }
  charAt(idx) {
    var idcs = this.indices, len = idcs.length;
    if (idx < 0 || idx >= len) return '';
    return (idx == len-1)
           ? this.original.substring(idcs[len-1])
           : this.original.substring(idcs[idx], idcs[idx+1]);
  }
  substring(startIdx, endIdx) {
    var idcs = this.indices, orig = this.original, ret = '';
    if (idcs.length == 0) return '';
    if (startIdx < 0) startIdx = 0;
    if (endIdx == null || endIdx < 0 || endIdx > idcs.length) endIdx = idcs.length;
    for (var i=startIdx; i<endIdx; i++)
      ret += orig.substring(idcs[i], idcs[i+1]);
    return ret;
  }
  substr(startIdx, endIdx) { return this.substring(startIdx, endIdx); }
  forEach (fn) { for (var i=0; i<this.length; i++) fn(this.charAt(i), i); }
}

const zpuncs = '，、；：。？！‧○';
const zpuncs1L = '「『《（';
const zpuncs1R = '」』》）—─…'; // '　' is punc?
const zpuncs1 = zpuncs1L + zpuncs1R;
const zpuncsAll = zpuncs + zpuncs1;
const REGEX_ZH = /[\u3300-\u4dbf]|[\u4e00-\u9fff]|[\uf900-\ufaff]|[\ufe30-\ufe4f]|[\u20000-\u2a6df]|[\u2a700-\u2ceaf]|[\u2f800-\u2fa1f]/;
function isPunc(z)  { return zpuncs.indexOf(z) >= 0; }
function isPunc1L(z) { return zpuncs1L.indexOf(z) >= 0; }
function isPunc1R(z) { return zpuncs1R.indexOf(z) >= 0; }
function isPunc1(z) { return zpuncs1.indexOf(z) >= 0; }
function isHanZi(x) { return !isASCII(x) && REGEX_ZH.test(x) && (zpuncsAll.indexOf(x) < 0); }
function isHanZiOrQuot(x) { return !isASCII(x) && REGEX_ZH.test(x) && (zpuncs.indexOf(x) < 0); }
function isASCII(str) { return /^[\x00-\xFF]*$/.test(str) }
function isWhite(c) { return c && c.length && !c.trim().length; }

function readTextFile(fn) { return require('fs').readFileSync(fn) + ''; }

function isAny(a, sample) {
  if (sample) {
    for (var i=sample.length-1; i>=0; i--)
      if (sample.charAt(i) == a) return true;
  }
  return false;
}

class Jian2ZhengRuleSet {
  constructor(verbose, isSpecial) {
    this.rules = {};
    this.verbose = verbose;
    this.isSpecial = isSpecial;
  }
  setRule(r) {
    this.rules[r.jianti] = r;
    if (this.isSpecial) r.errAsNull = true;
    return this;
  }
  setSimple(jt, zt) { return this.setRule(new OneOnOneRule(jt, zt)); }
  getRule(jt) { return this.rules[jt]; }
  convert(input, specialRules) {
    if (!input) return;
    if (typeof input == 'string') input = new Astral(input);
    var report = [], buf = new Buffer(), unknown = {};
    for (var i=0; i<input.getLength(); ++i) {
      var c = input.charAt(i);
      if (!isHanZi(c)) { buf.w(c); continue; }
      var r = specialRules && specialRules.getRule(c),
          tmp = r && r.result(input, i, report, this.verbose, true);
      if (tmp) {
        buf.w(tmp);
      } else {
        r = this.rules[c];
        if (r) {
          buf.w(r.result(input, i, report, this.verbose));
        } else {
          buf.w(c);
          unknown[c] = true;
        }
      }
    }
    unknown = Object.keys(unknown).join('');
    if (unknown) throw `Rules for ${unknown} not found.`;
    return { result:buf.render(), report };
  }
  parseRules(txt) {
    var a = txt.trim().split('\n');
    for (var i in a) this.parseRule(a[i]);
    return this;
  }
  parseRule(s) {
    function toAffix(s) { return (s.indexOf(',') > 0) ? s.split(',') : s; }
    if (s[0] != '[' || s[2] != ']') return;
    var r = new AffixRule(s[1]), a = s.substring(3).trim().split(' ');
    for (var i in a) {
      var part = a[i].trim();
      if (part[0] == '=') // e.g. "=&" or "=鐘"
        r.setAsis(part[1] == '&' ? null : part[1]);
      else if (part[1] == '<')
        r.prefix(part[0], toAffix(part.substring(2)));
      else if (part[1] == '>')
        r.suffix(part[0], toAffix(part.substring(2)));
    }
    this.setRule(r);
    return this;
  }
}

class Jian2ZhengRule {
  constructor(jt) { this.jianti = jt; }
  result(input, idx, msgObj, verbose, custom) { throw `Jian2ZhengRule.result() must be implemented.`; }
  addError(msgObj, input, idx) {
    if (!msgObj) return this;
    var zi = input.charAt(idx), target = '';
    for (var i in this) {
      if (i.startsWith('rule-'))
        target += i.substring(5);
    }
    msgObj.push(`??? [${idx+1}] ${zi} ${target||'　'} "${input.substring(idx-2, idx+2)}"`);
    return this;
  }
  addInfo(msgObj, input, idx, target, comment) { // usually when verbose
    if (!msgObj) return this;
    var zi = input.charAt(idx);
    if (target) target = '>' + target;
    else        target = '=' + zi;
    msgObj.push(`>>> [${idx+1}] (${zi}${target}) "${input.substring(idx-2, idx+3)}" {${comment||''}}`);
    return this;
  }
}

class OneOnOneRule extends Jian2ZhengRule {
  constructor(jt, zt) { super(jt); this.zhengti = zt || jt; }
  result(input, idx, msgObj, verbose) { return this.zhengti.charAt(0); }
}

const NUMBERS = '一二三四五六七八九十两百千万几数各多少每某';

class AffixRule extends Jian2ZhengRule {
  constructor(jt, errAsNull) { super(jt); this.asis = jt; this.errAsNull = errAsNull; }
  prefix(resultZi, prefix) {
    var rn = `rule-${resultZi}`, r = this[rn];
    if (!r) this[rn] = { prefix };
    else if (r.prefix) throw `${rn} prefix rule conflict.`;
    else r.prefix = prefix;
    return this;
  }
  suffix(resultZi, suffix) {
    var rn = `rule-${resultZi}`, r = this[rn];
    if (!r) this[rn] = { suffix };
    else if (r.suffix) throw `${rn} suffix rule conflict.`;
    else r.suffix = suffix;
    return this;
  }
  setAsis(asis) { this.asisOk = true; this.asis = asis || this.jianti; return this; }
  result(input, idx, msgObj, verbose, custom) {
    var sub, zi, type;
lp: for (var i in this) {
      if (i.startsWith('rule-')) {
        var r = this[i];
        zi = i.substring(5);
        if (typeof r.prefix == 'string') {
          type = `${zi}<${r.prefix}`;
          if (isAny(input.charAt(idx-1), r.prefix))
            break lp;
          type = zi = null;
        }
        else if (Array.isArray(r.prefix)) {
          type = `${zi}<${r.prefix.join(',')}`;
          sub = input.substring(idx-5, idx);
          for (var j in r.prefix)
            if (sub.endsWith(r.prefix[j])) break lp;
          type = zi = null;
        }
        if (typeof r.suffix == 'string') {
          zi = i.substring(5);
          type = `${zi}>${r.suffix}`;
          if (isAny(input.charAt(idx+1), r.suffix))
            break lp;
          type = zi = null;
        }
        else if (Array.isArray(r.suffix)) {
          zi = i.substring(5);
          type = `${zi}>${r.suffix.join(',')}`;
          sub = input.substring(idx+1, idx+5);
          for (var j in r.suffix)
            if (sub.startsWith(r.suffix[j])) break lp;
          type = zi = null;
        }
      }
    }
    if (!zi) {
      if (this.errAsNull) return null;
      if (!this.asisOk) this.addError(msgObj, input, idx);
    }
    zi = zi || this.asis;
    if (custom) custom = '*'; else custom = '';
    if (verbose) this.addInfo(msgObj, input, idx, zi, `${custom}${type||''}`);
    return zi;
  }
}

