function getSpecialRuleSet() { return new Jian2ZhengRuleSet(false, true); }

var customRules = {
  bfnn9055: getSpecialRuleSet().parseRules('[台] 台>家')
};

function convertFile(fname) {
  if (!fname) return;
  var input = readTextFile(fname),
      v = arguments[arguments.length-1] == 'verbose',
      c = arguments[1],
      x = getRuleSet(v).convert(input, customRules[c=='verbose' ? null : c]),
      len = x.report.length;
  if (len == 0) {
    console.log(x.result);
  } else {
    console.log('===============[正體結果]===============');
    console.log(x.result);
    console.log('========================================');
    for (var i=0; i<len; ++i)
      console.log(' ', x.report[i]);
  }
}

function testRZ(input, verbose) {
  var rs = getRuleSet(verbose), x = rs.convert(input, customRules['bfnn9055']);

  console.log();
  console.log('=========================================');
  console.log('[简入]', input);
  console.log('-----------------------------------------');
  console.log('[正出]', x.result);
  console.log('=========================================');
  if (x.report.length) {
    for (var i in x.report)
      console.log(' ', x.report[i]);
  }
}

var fn = process.argv[2];

if (fn == 'test') {
  testRZ('那本天台三观书摆在讲台上。', true);
  testRZ('她戴了一只手表，正举手了望，只是不知看见了什么。', true);
  testRZ('台家引以说止观', true);
  testRZ('不空罥索神变真言经。', true);
} else {
  var args = [], len = process.argv.length;
  for (var i=2; i<len; ++i)
    args.push(process.argv[i]);
  convertFile.apply(null, args);
}
